import React from "react";
import './App.css'

class App extends React.Component{
  state = {

    counters: [0, 0, 0],
};


incr = (i) => {

    this.setState(({ counters }) => {
        const newCount = [...counters];
        newCount[i] = newCount[i] + 1;
        return { counters: newCount };
    });
}


decr = (i) => {

  this.setState(({ counters }) => {
      const newCount = [...counters];
      newCount[i] = newCount[i] - 1;
      return { counters: newCount };
  });
}

render() {

    const { counters } = this.state;

    return (
        <div>

            {counters.map((counter, index) => (

                <Counter2
                    key={index}
                    index={index}
                    addValue={index + 1}
                    counter={counter}
                    incr={this.incr}
                    decr={this.decr}
                />
            ))}
        </div>
    );


}

}

function Counter2(props) {
  const { counter, incr, index ,decr} = props;
  <header> 
  <i className="fa fa-shopping-cart cart" aria-hidden="true"></i>
  number items
   </header>
  return (
      <>
          <p>the counter value is {counter}</p>
          <button onClick={() => incr(index)}>+</button>
          <button onClick={() => decr(index)}>-</button>
      </>
  );
}

export default App;
