import React from "react";

class Counter extends React.Component {

    state = {

        counters: [
            { value: 0, id: 0 },
            { value: 0, id: 1 },
            { value: 0, id: 2 },
        ],
        name:"",
    };


    incr = (e) => {

      const {name}= e.target;
      const i =name.split("-")[1];
        this.setState(({ counters }) => {
            const newCount = counters.map(({value, id})=>({value, id}));
            newCount[i] = { value: newCount[i].value +1, id: i};
            return { counters: newCount };
        });
    }


    render() {

        const { counters } = this.state;

        return (
            <div>

                {counters.map((value, id) => (

                    <Counter2
                        key={id}
                        name={`counter-${id}`}
                        counter={value}
                        incr={this.incr}
                    />
                ))}
            </div>
        );
    }

}

function Counter2(props) {
    const { counter, incr, name } = props;
    return (
        <>
            <h1>counters</h1>
            <p>the counter value is {counter}</p>
            <button onClick={incr} name={name}>
              +</button>
            {/* <button onClick={() => decr(index)}>-</button> */}
        </>
    );
}

export default Counter;
